import React from 'react'

// This is our category lists component. For now we just vary this with the labelling.
export function MediaCategoryList(props) {
  if (props.media === 'WATCH') {
    return <WatchMedia category={props.category} />
  }
  return <ListenMedia category={props.category} mediaType={props.mediaType} />
}

function WatchMedia({ category }) {
  return (
    <div className="category-list">
      <p className="category-title">{category}</p>
      <div className="category-media">
        <img
          src="https://image.tmdb.org/t/p/w600_and_h900_bestv2/5l10EjdgPxu8Gbl5Ww6SWkVQH6T.jpg"
          className="media-template-first"
          alt="media"
        />
        <img
          src="https://image.tmdb.org/t/p/w600_and_h900_bestv2/nMhv6jG5dtLdW7rgguYWvpbk0YN.jpg"
          className="media-template-middle"
          alt="media"
        />
        <img
          src="https://image.tmdb.org/t/p/w600_and_h900_bestv2/y55oBgf6bVMI7sFNXwJDrSIxPQt.jpg"
          className="media-template-middle"
          alt="media"
        />
        <img
          src="https://image.tmdb.org/t/p/w600_and_h900_bestv2/nMhv6jG5dtLdW7rgguYWvpbk0YN.jpg"
          className="media-template-last"
          alt="media"
        />
      </div>
    </div>
  )
}

function ListenMedia({ category, mediaType }) {
  const titleText = `${mediaType} Name`
  return (
    <div className="category-list">
      <p className="category-title">{category}</p>
      <div className="category-media">
        <ListenSet
          setClass="listen-set-first"
          imageSource="https://image.tmdb.org/t/p/w600_and_h900_bestv2/5l10EjdgPxu8Gbl5Ww6SWkVQH6T.jpg"
          title={titleText}
          subtext={`${mediaType} description here`}
          mediaClass="listen-media-first"
        />

        <ListenSet
          setClass="listen-set-middle"
          imageSource="https://image.tmdb.org/t/p/w600_and_h900_bestv2/nMhv6jG5dtLdW7rgguYWvpbk0YN.jpg"
          title={titleText}
          subtext={`${mediaType} description here`}
          mediaClass="listen-media-middle"
        />

        <ListenSet
          setClass="listen-set-middle"
          imageSource="https://image.tmdb.org/t/p/w600_and_h900_bestv2/y55oBgf6bVMI7sFNXwJDrSIxPQt.jpg"
          title={titleText}
          subtext={`${mediaType} description here`}
          mediaClass="listen-media-middle"
        />

        <ListenSet
          setClass="listen-set-last"
          imageSource="https://image.tmdb.org/t/p/w600_and_h900_bestv2/nMhv6jG5dtLdW7rgguYWvpbk0YN.jpg"
          title={titleText}
          subtext={`${mediaType} description here`}
          mediaClass="listen-media-last"
          alt="listen-media"
        />
      </div>
    </div>
  )
}

function ListenSet(props) {
  return (
    <div className={props.setClass}>
      <img
        src={props.imageSource}
        className={props.mediaClass}
        alt="listen-media"
      />
      <p className="listen-media-titles">{props.title}</p>
      <p className="listen-media-subtext">{props.subtext}</p>
    </div>
  )
}
