import React from "react";
import { Component } from "./WatchMedia";

export function ScrollList(){
    return (
        <div>
            <button src = "./WatchMedia" className = "prev" onClick= {() =>{
                document.getElementById("category-media").scroll({
                    right:30,
                    behaviour:'smooth',
                })
            }}
            />
            <button className = "next" onClick= {() =>{
                document.getElementById("category-media").scroll({
                    left:30,
                    behaviour:'smooth',
                })
            }}
            />
        <script src = "./WatchMedia"/>
        </div>
        
    );
}
