import React, { useState, useEffect } from "react";
import "./App.css";

import { AppHeader } from "./components/AppHeader";
import { AppBody } from "./components/AppBody";

import { Client } from "./client";

// Instantiate a single instance of the Client, used across
// the application for fetching of data.
const client = new Client("http://127.0.0.1:8000");

function App() {
  const [mediaSelected, setMediaSelected] = useState("WATCH");
  const [appState, setAppState] = useState({});

  useEffect(() => {
    if (mediaSelected === "WATCH") {
      getWatchData();
    } else {
      getListenData();
    }
  }, [mediaSelected]);

  const getWatchData = async () => {
    const data = await client.getWatchData();
    setAppState({ ...appState, watchData: data });
  };

  const getListenData = async () => {
    const data = await client.getListenData();
    setAppState({ ...appState, listenData: data });
  };

  return (
    <div className="App">
      <AppHeader />
      <AppBody
        mediaSelected={mediaSelected}
        onMediaChange={setMediaSelected}
        watch={{ data: appState.watchData, fetch: getWatchData }}
        listen={{ data: appState.listenData, fetch: getListenData }}
      />
    </div>
  );
}

export default App;
