import React, { Component } from "react";

export class SearchTV extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
      loaded: false,
      placeholder: "Loading"
    };
  }

  componentDidMount() {
    console.log("query passed is " + this.props.query);
    fetch(`http://localhost:8000/api/tv/${this.props.query}`)
      .then(response => {
        if (response.status > 400) {
          return this.setState(() => {
            return { placeholder: "Something went wrong!" };
          });
        }
        return response.json();
      })
      .then(data => {
        this.setState(() => {
          return {
            data,
            loaded: true
          };
        });
      });
  }

  render() {
    return <div className="category-title">{this.state.data.query}</div>;
  }
}
