import React from "react";
import { ListenSet } from "./ListenSet";
import { PodcastSet } from "./PodcastSet";

export function ListenMedia({ category, content, type, num }) {
  if (content !== undefined) {
    const contentStart = content.slice(0, 50);
    if (type === "Podcast" || type === "Playlist") {
      return (
        <div className="category-list">
          <p className="category-title">{category}</p>
          <div className="listen-category-media">
            <ListenMediaNav />
            {contentStart.map((item, index) => (
              <PodcastSet
                index={index}
                imageSource={item["imgURL"]}
                title={item["show_name"]}
                subtext={item["description"]}
                show_link={item["show_link"]}
                mediaClass="listen-media"
              />
            ))}
          </div>
        </div>
      );
    }
    return (
      <div className="category-list">
        <p className="category-title">{category}</p>
        <div className="listen-category-media">
          <ListenMediaNav />
          {contentStart.map((item, index) => (
            <ListenSet
              index={index}
              imageSource={item["imgURL"]}
              title={item["music_name"]}
              subtext={item["type"]}
              artist={item["artist_name"]}
              artist_link={item["artist_link"]}
              music_link={item["music_link"]}
              mediaClass="listen-media"
            />
          ))}
        </div>
      </div>
    );
  } else {
    return <div></div>;
  }
}

function ListenMediaNav() {
  return (
    <div className="nav-wrapper">
      <img
        src="https://img.icons8.com/cotton/64/000000/circled-chevron-right.png"
        alt="next"
        className="next"
        onClick={() => {
          document.getElementsByClassName("listen-category-media")[
            num
          ].scrollLeft += window.innerWidth;
        }}
      />
      <img
        src="https://img.icons8.com/cotton/64/000000/circled-chevron-left.png"
        alt="prev"
        className="prev"
        onClick={() => {
          document.getElementsByClassName("listen-category-media")[
            num
          ].scrollLeft -= window.innerWidth;
        }}
      />
    </div>
  );
}
