import React from "react";
import { MediaCategoryList } from "./MediaCategoryList";
import { MediaGenreList } from "./MediaGenreList";
import { WATCH_GENRES } from "../constants";

// This function will give us the complete watch experience!
export function WatchExperience({ watch, searchQuery }) {
  const { data } = watch;
  console.log("the current watch data looks like:");
  console.table(data);
  if (data.length > 0) {
    console.log("searchquery that is seen is" + searchQuery);
    if (searchQuery.length > 0) {
      console.log("about to render the following data:");
      console.table(data[`Search Results for ${searchQuery}`]);
      return (
        <div className="search-results">
          <div className="search-results-title">
            Search Results for {searchQuery}
          </div>
          <div className="watch-experience-lists">
            <MediaCategoryList
              category="TV Results"
              media="WATCH"
              mediaContent={
                data[`Search Results for ${searchQuery}`]["TV Results"]
              }
            />
            <MediaCategoryList
              category="Movie Results"
              media="WATCH"
              mediaContent={
                data[`Search Results for ${searchQuery}`]["Movie Results"]
              }
            />
          </div>
        </div>
      );
    }
    return (
      <div className="watch-experience-lists">
        <MediaCategoryList
          category="Trending Daily"
          media="WATCH"
          mediaContent={data["Trending Daily"]}
        />
        <MediaCategoryList
          category="Top Rated TV Shows"
          media="WATCH"
          mediaContent={data["Top Rated TV Shows"]}
        />
        <MediaCategoryList
          category="Now Airing TV Shows"
          media="WATCH"
          mediaContent={data["Now Airing TV Shows"]}
        />
        <MediaCategoryList
          category="Top Rated Movies"
          media="WATCH"
          mediaContent={data["Top Rated Movies"]}
        />
        <MediaGenreList media="WATCH" genres={WATCH_GENRES} />
      </div>
    );
  }
  // TODO: add loading state
  return null;
}
