import React, { useState } from "react";
import "./App.css";

import { MediaSelector } from "./components/MediaSelector";
import { OPTIONS, SEARCHTEXT } from "./components/constants";
import { Experience } from "./components/Experience";
// import { TestEndpoint } from "./TestEndpoint";

const searchQuery = { value: "h" };

export function App({ watchExperienceData, listenExperienceData }) {
  const [mediaSelected, setMediaSelected] = useState("WATCH");
  return (
    <div className="App">
      <header className="App-header">
        <p className="myLounge-heading">myLounge</p>
        <p className="myLounge-subtitle">Listen. Watch. Relax.</p>
        <SearchBar
          name="searchbar-menu"
          inputname="searchinput-menu"
          imagename="searchbutton-menu"
          placeholder="Search for Movie, TV Show, Podcast or Music by name..."
        />
      </header>
      <section className="App-media-options">
        <div className="filters-header">
          <div className="media-selection">
            <p className="selection-label">I want to</p>
            {OPTIONS.map(option => (
              <MediaSelector
                key={option}
                option={option}
                isSelected={mediaSelected === option}
                onClick={option => {
                  setMediaSelected(option);
                }}
              />
            ))}
          </div>
          <SearchBar
            name="searchbar"
            inputname="searchinput"
            imagename="searchbutton"
            placeholder={SEARCHTEXT[mediaSelected]}
          />
          <div className="category-title">{searchQuery["value"]}</div>
        </div>
        <div id="media-options-window">
          <Experience
            currentExperience={mediaSelected}
            watchData={watchExperienceData}
            listenData={listenExperienceData}
          />
        </div>
      </section>
    </div>
  );
}

class SearchBar extends React.Component {
  constructor(props) {
    super(props);
    this.state = { value: "" };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    console.log("current handlechange value: " + this.state.value);
    this.setState({ value: event.target.value });
    if (event.target.value === "") {
      document.getElementById("media-options-window").style.display = "flex";
    }
  }

  handleSubmit(event) {
    if (this.state.value !== "") {
      document.getElementById("media-options-window").style.display = "none";
      searchQuery["value"] = this.state.value;
    }
    console.log("Search Query: " + this.state.value);
    event.preventDefault();
  }

  render() {
    return (
      <form onSubmit={this.handleSubmit} className={this.props.name}>
        <img
          src="https://img.icons8.com/android/24/000000/search.png"
          className={this.props.imagename}
          alt="searchIcon"
        ></img>
        <input
          className={this.props.inputname}
          type="text"
          placeholder={this.props.placeholder}
          onChange={this.handleChange}
        />
        <input type="submit" value="Submit" className="hide-this" />
      </form>
    );
  }
}
