import React from "react";

function WatchMediaMetadata({ airDate, language, rating }) {
  return [airDate, language, rating].filter((item) => !!item).join(" | ");
}

export function WatchMedia({
  category,
  content,
  getOverlayData,
  setOpenOverlayCategory,
  num,
}) {
  if (content !== undefined) {
    const contentStart = content.slice(0, 20);
    return (
      <div className="category-list">
        <p className="category-title">{category}</p>
        <div className="category-media">
          <div className="nav-wrapper">
            <svg
              xmlns="http://www.w3.org/2000/svg"
              x="0px"
              y="0px"
              width="64"
              height="64"
              viewBox="0 0 172 172"
              style=" fill:#000000;"
            >
              <g
                fill="none"
                fill-rule="nonzero"
                stroke="none"
                stroke-width="1"
                stroke-linecap="butt"
                stroke-linejoin="miter"
                stroke-miterlimit="10"
                stroke-dasharray=""
                stroke-dashoffset="0"
                font-family="none"
                font-weight="none"
                font-size="none"
                text-anchor="none"
                style="mix-blend-mode: normal"
              >
                <path d="M0,172v-172h172v172z" fill="#ffffff"></path>
                <g fill="#5f5f5f">
                  <path d="M86,6.88c-43.65844,0 -79.12,35.46156 -79.12,79.12c0,43.65844 35.46156,79.12 79.12,79.12c43.65844,0 79.12,-35.46156 79.12,-79.12c0,-43.65844 -35.46156,-79.12 -79.12,-79.12zM86,13.76c39.93625,0 72.24,32.30375 72.24,72.24c0,39.93625 -32.30375,72.24 -72.24,72.24c-39.93625,0 -72.24,-32.30375 -72.24,-72.24c0,-39.93625 32.30375,-72.24 72.24,-72.24zM75.3575,48.0525c-0.14781,0.02688 -0.29562,0.06719 -0.43,0.1075c-1.29,0.22844 -2.32469,1.16906 -2.6875,2.41875c-0.36281,1.26313 0.01344,2.60688 0.9675,3.49375l31.9275,31.9275l-31.9275,31.9275c-1.37062,1.37063 -1.37062,3.57438 0,4.945c1.37063,1.37063 3.57438,1.37063 4.945,0l34.4,-34.4c0.67188,-0.645 1.04813,-1.54531 1.04813,-2.4725c0,-0.92719 -0.37625,-1.8275 -1.04813,-2.4725l-34.4,-34.4c-0.71219,-0.76594 -1.74687,-1.15562 -2.795,-1.075z"></path>
                </g>
              </g>
            </svg>

            <img
              src="https://img.icons8.com/cotton/64/000000/circled-chevron-right.png"
              alt="next"
              className="next"
              onClick={() => {
                document.getElementsByClassName("category-media")[
                  num
                ].scrollLeft += 0.5 * window.innerWidth;
              }}
            />
            <img
              src="https://img.icons8.com/cotton/64/000000/circled-chevron-left.png"
              alt="prev"
              className="prev"
              onClick={() => {
                document.getElementsByClassName("category-media")[
                  num
                ].scrollLeft -= 0.3 * window.innerWidth;
              }}
            />
          </div>
          {contentStart.map((item, index) => (
            <div className="media-template">
              <div className="media-image-wrapper">
                <div className="media-user-score">
                  <span className="user-score-text">
                    {Math.round(item["score"] * 1000) / 10}%
                  </span>
                </div>
                <img
                  index={index}
                  src={item["imgURL"]}
                  className="media-image"
                  alt="media"
                  onClick={() => {
                    getOverlayData(item["id"], item["type"]);
                  }}
                />
              </div>
              <p className="watch-title">{item["name"]}</p>
              <div className="watch-metadata">
                <WatchMediaMetadata
                  airDate={item["first_air_date"]}
                  language={item["lang"]}
                  rating={item["content_rating"]}
                />
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  } else {
    return <div></div>;
  }
}
