import React, { useState } from "react";
import "./App.css";

import { SearchBar } from "./components/SearchBar";
import { MediaSelector } from "./components/MediaSelector";
import { OPTIONS, SEARCHTEXT } from "./components/constants";
import { Experience } from "./components/Experience";

function Header(props) {
  return (
    <div className="App">
      <header className="App-header">
        <p className="myLounge-heading">myLounge</p>
        <p className="myLounge-subtitle">Listen. Watch. Relax.</p>
        <SearchBar
          name="searchbar-menu"
          inputname="searchinput-menu"
          imagename="searchbutton-menu"
          placeholder="Search for Movie, TV Show, Podcast or Music by name..."
        />
      </header>
    </div>
  );
}

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = { value: this.props.placeholder };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  handleChange(event) {
    if (event.target.value === "") {
      this.setState({ value: event.target.value });
    }
  }

  handleSubmit(event) {
    this.setState({ value: event.target.value });
    event.preventDefault();
  }

  render() {
    const [mediaSelected, setMediaSelected] = useState("WATCH");
    if (this.state.value === "") {
      return (
        <div className="App">
          <Header />
          <section className="App-media-options">
            <div className="filters-header">
              <div className="media-selection">
                <p className="selection-label">I want to</p>
                {OPTIONS.map(option => (
                  <MediaSelector
                    key={option}
                    option={option}
                    isSelected={mediaSelected === option}
                    onClick={option => {
                      setMediaSelected(option);
                    }}
                  />
                ))}
              </div>
              <form onSubmit={this.handleSubmit} className="searchbar-menu">
                <img
                  src="https://img.icons8.com/android/24/000000/search.png"
                  className={this.props.imagename}
                  alt="searchIcon"
                ></img>
                <input
                  className={this.props.inputname}
                  type="text"
                  placeholder={this.props.placeholder}
                  onChange={this.handleChange}
                />
              </form>
            </div>
            <Experience currentExperience={mediaSelected} />
          </section>
        </div>
      );
    } else {
      return (
        <div className="App">
          <Header />
          <section className="App-media-options">
            <div className="filters-header">
              <div className="media-selection">
                <p className="selection-label">I want to</p>
                {OPTIONS.map(option => (
                  <MediaSelector
                    key={option}
                    option={option}
                    isSelected={mediaSelected === option}
                    onClick={option => {
                      setMediaSelected(option);
                    }}
                  />
                ))}
              </div>
              <form onSubmit={this.handleSubmit} className="searchbar-menu">
                <img
                  src="https://img.icons8.com/android/24/000000/search.png"
                  className={this.props.imagename}
                  alt="searchIcon"
                ></img>
                <input
                  className={this.props.inputname}
                  type="text"
                  placeholder={this.props.placeholder}
                  onChange={this.handleChange}
                />
              </form>
            </div>
            <Experience
              currentExperience={mediaSelected}
              currentQuery={this.state.value}
            />
          </section>
        </div>
      );
    }
  }
}

export default App;
