import React from "react";

export function ScrollList(){

    return (
        <div>
            <button className = "prev" 
                    onClick= {() =>{
                        document.getElementById("category-media").scroll({
                            right:30,
                            behaviour:'smooth',
                        })
                    }}
            />
            <button className = "next" 
                    onClick= {() =>{
                        document.getElementById("category-media").scroll({
                            left:30,
                            behaviour:'smooth',
                        })
                    }}
            />
        </div>


    );


}
